.flex-container {
  display: flex;
  flex-wrap: nowrap;
  background-color: DodgerBlue;
  justify-content: center; }

.flex-container > div {
  background-color: dodgerblue;
  width: 100px;
  margin: 10px;
  text-align: center;
  line-height: 75px;
  font-size: 30px; }

a {
  text-decoration: none;
  color: black; }

@media screen and (max-width: 380px) {
  img {
    padding: 10px 0px 0px 0px;
    width: 100%;
    height: 70%; } }
.size {
  margin-top: 20px;
  margin-bottom: 20px; }

.box {
  display: flex;
  align-items: center;
  flex-direction: column; }

adjust {
  align-items: center; }

textarea {
  width: 350px;
  height: 50px;
  margin-top: 50px; }

input {
  width: 350px;
  margin-top: 50px; }

h1 {
  text-align: center;
  font-size: 30px; }

